import UIKit

@objc protocol WeatherRoutingLogic
{
    func routeToForecast()
}

protocol WeatherDataPassing
{
  var dataStore: WeatherDataStore? { get }
}

class WeatherRouter: BaseRouter, WeatherRoutingLogic, WeatherDataPassing {

  var dataStore: WeatherDataStore?
  
  // MARK: Routing
  
    func routeToForecast() {
        let vc = getViewController(storyboard: .Forecast, expectedVC: ForeCastViewController.self)
        guard let lat = dataStore?.lat else { return }
        guard let lon = dataStore?.lon else { return }
        var destination = vc.router?.dataStore
        destination?.lat = lat
        destination?.lon = lon
        pushViewController(vc)
    }
}
